import React, { useState } from 'react';
import axios from 'axios';

const ResumeUploader = () => {
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const [analysis, setAnalysis] = useState(null); // New state for analysis

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMessage('');
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      setMessage('Please select a PDF file.');
      setAnalysis(null); // Clear previous analysis
      return;
    }
    const formData = new FormData();
    formData.append('resume', file);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setMessage(res.data.message);
      setAnalysis(res.data.analysis || null); // Set analysis if present
    } catch (err) {
      setMessage(
        err.response?.data?.message || 'Upload failed. Please try again.'
      );
      setAnalysis(null); // Clear analysis on error
    }
  };

  return (
    <div>
      <h2>Upload Resume (PDF)</h2>
      <form onSubmit={handleUpload}>
        <input
          type="file"
          accept="application/pdf"
          onChange={handleFileChange}
        />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}
      {analysis && (
        <div>
          <h3>Resume Analysis Result:</h3>
          <pre style={{ textAlign: 'left', background: '#f4f4f4', padding: '1em', borderRadius: '5px', overflowX: 'auto' }}>
            {JSON.stringify(analysis, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
};

export default ResumeUploader;
