import React, { useState } from 'react';
import ResumeUploader from './components/ResumeUploader';
import PastResumesTable from './components/PastResumesTable/PastResumesTable';

function App() {
  const [activeTab, setActiveTab] = useState('analyze');
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    if (tab === 'history') {
      setAnalysis(null);
      setMessage('');
      setLoading(false);
    }
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'center', margin: '24px 0' }}>
        <button
          onClick={() => handleTabChange('analyze')}
          style={{
            padding: '10px 24px',
            marginRight: 8,
            background: activeTab === 'analyze' ? '#3498db' : '#f4f4f4',
            color: activeTab === 'analyze' ? '#fff' : '#333',
            border: 'none',
            borderRadius: 6,
            cursor: 'pointer',
            fontWeight: 600,
          }}
        >
          Live Resume Analysis
        </button>
        <button
          onClick={() => handleTabChange('history')}
          style={{
            padding: '10px 24px',
            background: activeTab === 'history' ? '#3498db' : '#f4f4f4',
            color: activeTab === 'history' ? '#fff' : '#333',
            border: 'none',
            borderRadius: 6,
            cursor: 'pointer',
            fontWeight: 600,
          }}
        >
          History
        </button>
      </div>
      {activeTab === 'analyze' && (
        <ResumeUploader
          analysis={analysis}
          setAnalysis={setAnalysis}
          loading={loading}
          setLoading={setLoading}
          message={message}
          setMessage={setMessage}
        />
      )}
      {activeTab === 'history' && <PastResumesTable />}
    </div>
  );
}

export default App;
